SELECT s.business_id, s.business_title, s.client_id, s.contact_id, s.sales_amount, s.signing_date, s.sales_phase, s.business_attention, s.product_id, s.follow_up_time, s.business_comment, s.project_contact, s.post, s.project_contact_phone, tcm.customer_id, tcm.customer_name, tcm.customer_status, tcm.create_time, tct.contact_id, tct.contact_name, tct.contact_gender, tct.contact_phone, tct.contact_landline, tct.dept_id, tct.contact_position, tct.create_time, tct.contact_status, tpd.product_id, tpd.product_skuId, tpd.product_name, tpd.product_brand, tpd.membership_price, tpd.wholesale_price, tpd.product_model, tpd.product_level, tpd.create_time, tpd.product_status, tfur.followup_id, tfur.contact_id, tfur.followup_method, tfur.create_time, tfur.actual_time, tfur.followup_content, tfur.business_id FROM (SELECT * FROM t_business tb LIMIT #{business.page},#{business.size}) AS s LEFT JOIN t_consumer tcm ON tcm.customer_id = s.client_id LEFT JOIN t_contact tct ON tct.contact_id = s.contact_id LEFT JOIN t_product tpd ON tpd.product_id = s.product_id LEFT JOIN t_followup_record tfur ON tfur.business_id = s.business_id
and s.business_id = #{business.businessId}
AND s.business_title LIKE #{parameter}
AND s.client_id = #{business.clientId}
AND s.contact_id = #{business.contactId}
AND s.sales_amount = #{business.salesAmount}
AND s.signing_date BETWEEN #{business.startTime} AND #{business.endTime}

SELECT COUNT(1)
FROM t_business  s
         LEFT JOIN t_consumer tcm ON tcm.customer_id = s.client_id
         LEFT JOIN t_contact tct ON tct.contact_id = s.contact_id
         LEFT JOIN t_product tpd ON tpd.product_id = s.product_id
         LEFT JOIN t_followup_record tfur ON tfur.business_id = s.business_id


        and s.business_id = #{business.businessId}



        AND s.business_title LIKE #{parameter}


        AND s.client_id = #{business.clientId}


        AND s.contact_id = #{business.contactId}


        AND s.sales_amount = #{business.salesAmount}


        AND s.signing_date BETWEEN #{business.startTime} AND #{business.endTime}
